function DFS(node, P, failure_function, current_path, result):
    for each child in node.children:
        edge_label = getEdgeLabel(node, child)
        current_path += edge_label

        // Find all occurrences of P in current_path
        occurrences = KMP(current_path, P, failure_function)

        for each occurrence in occurrences:
            subpath = current_path[occurrence : occurrence + len(P)]
            result.append(subpath)

        DFS(child, P, failure_function, current_path, result)

        // Backtrack
        current_path = current_path[: -len(edge_label)]

function KMP(text, pattern, failure_function):
    occurrences = []
    j = 0  // Index for pattern
    for i in range(len(text)):
        while j &gt; 0 and text[i] != pattern[j]:
            j = failure_function[j - 1]

        if text[i] == pattern[j]:
            j += 1

        if j == len(pattern):
            occurrences.append(i - j + 1)
            j = failure_function[j - 1]

    return occurrences

